/* TODO */
table {

    border-collapse: collapse;
    table-layout: fixed;
    width: 100%;
    @media (max-width: 768px) {
        table-layout: auto;
        overflow-x: scroll;
        max-width: 100vw;
    }
  }
  thead tr {
    border-bottom: 1px solid $ghost-border;
    border-top: 1px solid $ghost-border;
    height: 1px;
  }
  th {
    height: inherit;
    padding: 0;
    color: $texte;
    text-align: left;
    background-color: $fond-3;
  }
  th:not(:first-of-type) {
    border-left: 1px solid $ghost-border;
  }
  th button {
    background-color: transparent;
    border: none;
    cursor: pointer;
    display: block;
    font: inherit;
    height: 100%;
    margin: 0;
    min-width: max-content;
    padding: 0.5rem 1rem;
    position: relative;
    text-align: left;
    width: 100%;
    color: $texte;
    font-weight: 300;
    height: 60px;
  }
  th button[data-dir] {
    background-color: $ghost-border;
  }
